-- Create database if it doesn't exist
CREATE DATABASE IF NOT EXISTS cgi_rr;

-- Use the database
USE cgi_rr;

-- Disable foreign key checks
SET FOREIGN_KEY_CHECKS = 0;

-- Drop tables if they exist
DROP TABLE IF EXISTS approvalentry;
DROP TABLE IF EXISTS rewardsentry;
DROP TABLE IF EXISTS leaderboards;
DROP TABLE IF EXISTS rewardpointscriteria;
DROP TABLE IF EXISTS members;

-- Re-enable foreign key checks
SET FOREIGN_KEY_CHECKS = 1;

-- Create tables
CREATE TABLE IF NOT EXISTS members (
  id int unsigned NOT NULL AUTO_INCREMENT,
  member_employee_id varchar(255) NOT NULL,
  member_username varchar(255) NOT NULL,
  member_firstname varchar(255) NOT NULL,
  member_lastname varchar(255) NOT NULL,
  member_email varchar(255) NOT NULL,
  member_title varchar(255) NOT NULL,
  member_manager_id varchar(255) DEFAULT NULL,
  member_director_id varchar(255) DEFAULT NULL,
  role_id int NOT NULL,
  created_at timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  updated_at timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  member_status varchar(45) NOT NULL,
  login_time timestamp NULL DEFAULT NULL,
  logout_time timestamp NULL DEFAULT NULL,
  hidePopup tinyint DEFAULT '0',
  PRIMARY KEY (id),
  UNIQUE KEY members_member_username_unique (member_username),
  UNIQUE KEY members_member_email_unique (member_email),
  UNIQUE KEY member_employee_id_UNIQUE (member_employee_id)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;

CREATE TABLE IF NOT EXISTS leaderboards (
  id int NOT NULL AUTO_INCREMENT,
  member_employee_id varchar(50) NOT NULL,
  alias_name varchar(50) DEFAULT NULL,
  fiscal_year varchar(50) DEFAULT NULL,
  total_points int DEFAULT '0',
  approved_points int DEFAULT '0',
  for_approval_points int DEFAULT '0',
  rejected_points int DEFAULT '0',
  created_at timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  updated_at timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  PRIMARY KEY (id),
  UNIQUE KEY member_employee_id (member_employee_id),
  CONSTRAINT leaderboards_ibfk_1 FOREIGN KEY (member_employee_id) REFERENCES members (member_employee_id)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;

CREATE TABLE IF NOT EXISTS rewardpointscriteria (
  id int NOT NULL AUTO_INCREMENT,
  category varchar(100) DEFAULT NULL,
  accomplishment text,
  points int DEFAULT NULL,
  guidelines text,
  director_approval tinyint(1) DEFAULT NULL,
  created_at timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  updated_at timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  PRIMARY KEY (id)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;

CREATE TABLE IF NOT EXISTS rewardsentry (
  id int NOT NULL AUTO_INCREMENT,
  short_description text,
  member_employee_id varchar(50) DEFAULT NULL,
  criteria_id int DEFAULT NULL,
  date_accomplished date DEFAULT NULL,
  race_season varchar(50) DEFAULT NULL,
  cbps_group varchar(100) DEFAULT NULL,
  project_name varchar(150) DEFAULT NULL,
  notes text,
  attachments varchar(255) DEFAULT NULL,
  created_at timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  updated_at timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  PRIMARY KEY (id),
  KEY member_employee_id (member_employee_id),
  KEY criteria_id (criteria_id),
  CONSTRAINT rewardsentry_ibfk_1 FOREIGN KEY (member_employee_id) REFERENCES members (member_employee_id),
  CONSTRAINT rewardsentry_ibfk_2 FOREIGN KEY (member_employee_id) REFERENCES leaderboards (member_employee_id),
  CONSTRAINT rewardsentry_ibfk_3 FOREIGN KEY (criteria_id) REFERENCES rewardpointscriteria (id)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;

CREATE TABLE IF NOT EXISTS approvalentry (
  id int NOT NULL AUTO_INCREMENT,
  rewards_id int DEFAULT NULL,
  manager_id varchar(100) DEFAULT NULL,
  director_id varchar(100) DEFAULT NULL,
  manager_approval_status enum('approved','pending','rejected') DEFAULT 'pending',
  director_approval_status enum('approved','pending','rejected') DEFAULT 'pending',
  manager_notes text,
  director_notes text,
  created_at timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  updated_at timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  PRIMARY KEY (id),
  KEY rewards_id (rewards_id),
  KEY manager_id (manager_id),
  KEY director_id (director_id),
  CONSTRAINT approvalentry_ibfk_1 FOREIGN KEY (rewards_id) REFERENCES rewardsentry (id),
  CONSTRAINT approvalentry_ibfk_2 FOREIGN KEY (manager_id) REFERENCES members (member_employee_id),
  CONSTRAINT approvalentry_ibfk_3 FOREIGN KEY (director_id) REFERENCES members (member_employee_id)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;

